import{_ as i,c as r,o as t,ag as l}from"./chunks/framework.CUlIj1Ah.js";const h=JSON.parse('{"title":"Agile & Scrum","description":"","frontmatter":{},"headers":[],"relativePath":"ressources/agile-scrum.md","filePath":"ressources/agile-scrum.md"}'),a={name:"ressources/agile-scrum.md"};function o(s,e,n,u,c,d){return t(),r("div",null,[...e[0]||(e[0]=[l('<h1 id="agile-scrum" tabindex="-1">Agile &amp; Scrum <a class="header-anchor" href="#agile-scrum" aria-label="Permalink to &quot;Agile &amp; Scrum&quot;">​</a></h1><h2 id="terminologies-essentielles" tabindex="-1">Terminologies essentielles <a class="header-anchor" href="#terminologies-essentielles" aria-label="Permalink to &quot;Terminologies essentielles&quot;">​</a></h2><h3 id="produit-projet" tabindex="-1">Produit / Projet <a class="header-anchor" href="#produit-projet" aria-label="Permalink to &quot;Produit / Projet&quot;">​</a></h3><ul><li><strong>Produit</strong> : l’application que vous développez durant la session.</li><li><strong>Projet</strong> : l’ensemble du travail planifié et réalisé (planification, développement, suivi, présentations).</li></ul><hr><h3 id="sprint" tabindex="-1">Sprint <a class="header-anchor" href="#sprint" aria-label="Permalink to &quot;Sprint&quot;">​</a></h3><ul><li><strong>Sprint</strong> : cycle de développement court (quelques semaines).</li><li>Chaque sprint a un <strong>objectif clair</strong> et se termine par une livraison (démo + rapport).</li></ul><hr><h3 id="backlog" tabindex="-1">Backlog <a class="header-anchor" href="#backlog" aria-label="Permalink to &quot;Backlog&quot;">​</a></h3><ul><li><strong>Product Backlog</strong> : liste globale de toutes les fonctionnalités prévues. (équivaut à votre planification initiale du projet).</li><li><strong>Sprint Backlog</strong> : sous-liste de tâches choisies pour un sprint spécifique (planification de sprint).</li></ul><hr><h3 id="planification-du-sprint" tabindex="-1">Planification du sprint <a class="header-anchor" href="#planification-du-sprint" aria-label="Permalink to &quot;Planification du sprint&quot;">​</a></h3><ul><li>Rencontre au début du sprint.</li><li>Sélection des tâches à accomplir.</li><li>Estimation des efforts (heures) et répartition du travail.</li></ul><hr><h3 id="tache" tabindex="-1">Tâche <a class="header-anchor" href="#tache" aria-label="Permalink to &quot;Tâche&quot;">​</a></h3><ul><li>Plus petit élément de travail identifié dans votre backlog.</li><li>Chaque tâche doit avoir : un titre clair, une estimation, un responsable, et parfois une dépendance.</li></ul><hr><h3 id="kanban" tabindex="-1">Kanban <a class="header-anchor" href="#kanban" aria-label="Permalink to &quot;Kanban&quot;">​</a></h3><ul><li>Outil visuel de suivi des tâches.</li><li>Colonnes utilisées dans ce cours : <ul><li><strong>Backlog</strong> : toutes les idées de tâches.</li><li><strong>À faire</strong> : tâches choisies pour le sprint.</li><li><strong>En cours</strong> : tâches en développement.</li><li><strong>Terminée</strong> : tâches complétées.</li><li><strong>Bogues et finition</strong> : problèmes découverts à corriger et tâches de finition.</li></ul></li></ul><hr><h3 id="melee-courte-daily-scrum" tabindex="-1">Mêlée courte (Daily Scrum) <a class="header-anchor" href="#melee-courte-daily-scrum" aria-label="Permalink to &quot;Mêlée courte (Daily Scrum)&quot;">​</a></h3><ul><li>Brève rencontre (en classe ou avec le professeur) pour : <ul><li>dire ce qui a été fait,</li><li>dire ce qui reste à faire,</li><li>signaler les blocages.</li></ul></li></ul><hr><h3 id="criteres-de-completion" tabindex="-1">Critères de complétion <a class="header-anchor" href="#criteres-de-completion" aria-label="Permalink to &quot;Critères de complétion&quot;">​</a></h3><ul><li>Définition des conditions qui indiquent qu’une tâche ou un sprint est terminé avec succès.</li><li>Exemple : <em>« L’utilisateur peut sauvegarder son profil et voir un message de confirmation ».</em></li></ul><hr><h3 id="demo-review" tabindex="-1">Démo (Review) <a class="header-anchor" href="#demo-review" aria-label="Permalink to &quot;Démo (Review)&quot;">​</a></h3><ul><li>Présentation du travail à la fin du sprint.</li><li>Vous montrez les fonctionnalités réalisées, en lien avec vos objectifs.</li></ul><hr><h3 id="retrospective" tabindex="-1">Rétrospective <a class="header-anchor" href="#retrospective" aria-label="Permalink to &quot;Rétrospective&quot;">​</a></h3><ul><li>Analyse de votre sprint après la démo.</li><li>Trois questions clés : <ul><li>Qu’est-ce qui a bien fonctionné ?</li><li>Qu’est-ce qui a moins bien fonctionné ?</li><li>Que va-t-on améliorer pour le prochain sprint ?</li></ul></li></ul><hr><h2 id="roles-adaptes-pour-le-cours" tabindex="-1">Rôles adaptés pour le cours <a class="header-anchor" href="#roles-adaptes-pour-le-cours" aria-label="Permalink to &quot;Rôles adaptés pour le cours&quot;">​</a></h2><ul><li><strong>Équipe</strong> : développeurs (vous).</li><li><strong>Professeur</strong> : agit comme <em>Product Owner</em> (valide et guide le projet et le backlog).</li><li><strong>Scrum Master</strong> : optionnel; peut être pris par un membre pour organiser l’équipe.</li></ul>',34)])])}const m=i(a,[["render",o]]);export{h as __pageData,m as default};
